// src/components/Recommendation.js
import React, { useState } from 'react';
import axios from 'axios';
import SongCard from './SongCard';
import '../css/Recommendation.css';

function Recommendation() {
    const [query, setQuery] = useState('');
    const [songs, setSongs] = useState([]);

    // Function to handle search
    const handleSearch = async () => {
        try {
            const response = await axios.get(`http://localhost:5000/api/spotify/search`, {
                params: { q: query },
            });
            setSongs(response.data); // Set songs with the response data
        } catch (error) {
            console.error('Error fetching songs:', error);
        }
    };

    return (
        <div className="recommendation">
            <h2>insert a song to get started!</h2>
            <div className="search-container">
                <input
                    type="text"
                    placeholder="song name..."
                    value={query}
                    onChange={(e) => setQuery(e.target.value)}
                />
                <button className="generate-btn" onClick={handleSearch}>
                    generate
                </button>
            </div>
            <h3>you might like these</h3>
            <div className="song-list">
                {songs.map((song) => (
                    <SongCard
                        key={song.id}
                        name={song.name}
                        artist={song.artists[0].name}
                    />
                ))}
            </div>
        </div>
    );
}

export default Recommendation;
